scripts.build:
pnpm --package typescript dlx tsc -d -m nodenext -t es2021 microdb.ts --outDir dist && sed -Ei 's/"use strict";

//g' dist/microdb.js && sed -Ei ':a;N;$!ba;s/
 +private _[^
]+//g;s/export \{\};//g' dist/microdb.d.ts

scripts.publish:
pnpm run build && cp package.json package.json.orig && sed -Ei ':a;N;$!ba;s|"main"[^
]+|"main": "./dist/microdb.js",
  "module": "./dist/microdb.js",
  "types": "./dist/microdb.d.ts",|;s|,
  "scripts".*$|
}|' package.json && pnpm publish --access public --no-git-checks; mv package.json.orig package.json
